import React, { useState } from "react";
import AnimalGrid from "../components/AnimalGrid/AnimalGrid";
import ColorPicker from "../components/ColorPicker/ColorPicker";
import ColorDisplay from "../components/ColorDisplay/ColorDisplay";
import ScoreBoard from "../components/ScoreBoard/ScoreBoard";

export default function SenderScreen({
  animals,
  refreshPositions,
  timer,
  senderColor,
  score,
  onColorChange,
  onRoleSwitch, // Callback to switch roles
}) {
  const [isFadingOut, setIsFadingOut] = useState(false);

  // Trigger fade-out animation before switching roles
  const handleRoleSwitch = () => {
    setIsFadingOut(true); // Start fade-out animation
    setTimeout(() => {
      setIsFadingOut(false); // Reset fade-out state
      onRoleSwitch(); // Switch roles after animation
    }, 500); // Match the duration of the fade-out animation (0.5s)
  };

  return (
    <>
      <AnimalGrid animals={animals} refreshPositions={refreshPositions} role="sender" />
      <div className={`color-picker-container ${isFadingOut ? "fade-out" : ""}`}>
        <ColorPicker onColorChange={onColorChange} />
      </div>
      <div className="color-display-container">
        <ColorDisplay color={senderColor} />
        <div className="color-display">
          <p style={{ fontSize: "2em", fontWeight: "bold" }}>
            {timer > 0 ? `Timer: ${timer}s` : "Time's up!"}
          </p>
        </div>
      </div>
      <ScoreBoard score={score} />
      <button onClick={handleRoleSwitch}>Switch to Receiver</button>
    </>
  );
}
