import React, { useEffect, useRef } from "react";

const ColorPicker = ({ onColorChange }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    let radius = Math.min(canvas.width / 2, canvas.height / 2);

    // Draw color wheel programmatically
    const drawWheel = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      for (let angle = 0; angle < 360; angle += 1) {
        const startAngle = (angle - 2) * (Math.PI / 180);
        const endAngle = angle * (Math.PI / 180);
        ctx.beginPath();
        ctx.moveTo(canvas.width / 2, canvas.height / 2);
        ctx.arc(
          canvas.width / 2,
          canvas.height / 2,
          radius,
          startAngle,
          endAngle
        );
        ctx.fillStyle = `hsl(${angle}, 100%, 50%)`;
        ctx.fill();
      }
    };

    const resizeCanvas = () => {
      const parent = canvas.parentElement;
      canvas.width = parent.offsetWidth;
      canvas.height = parent.offsetHeight;
      radius = Math.min(canvas.width / 2, canvas.height / 2);
      drawWheel();
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);
    return () => window.removeEventListener("resize", resizeCanvas);
  }, []);

  const handleCanvasClick = (event) => {
    const canvas = canvasRef.current;
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left - canvas.width / 2;
    const y = event.clientY - rect.top - canvas.height / 2;
    const [r, theta] = cartesianToPolar(x, y);

    if (r > Math.min(canvas.width / 2, canvas.height / 2)) return;

    const hue = (theta * (180 / Math.PI) + 360) % 360;
    const color = `hsl(${hue}, 100%, 50%)`;
    onColorChange(color);
  };

  return (
    <canvas
      ref={canvasRef}
      className="color-picker"
      onClick={handleCanvasClick}
    />
  );
};

// Convert mouse coordinates to polar (radius/angle)
const cartesianToPolar = (x, y) => {
  const r = Math.sqrt(x * x + y * y);
  const theta = Math.atan2(y, x);
  return [r, theta];
};

export default ColorPicker;
