import React, { useEffect, useState, useRef } from "react";

export default function ColorDisplay({ color }) {
  const [displayColor, setDisplayColor] = useState("#FFFFFF"); // Default to white
  const [opacity, setOpacity] = useState(1); // Default opacity
  const timeoutRef = useRef(null);

  useEffect(() => {
    // Clear any previous timeout
    if (timeoutRef.current) clearTimeout(timeoutRef.current);

    // Reset to white and full opacity immediately before setting the new color
    setDisplayColor("#FFFFFF");
    setOpacity(1);

    // Only set the new color and start the timer if the color is not white
    if (color && color.toUpperCase() !== "#FFFFFF") {
      setTimeout(() => {
        setDisplayColor(color); // Set the new color
      }, 0); // Delay by 0ms to ensure reset happens first

      timeoutRef.current = setTimeout(() => {
        setOpacity(0); // Fade out after 2 seconds
      }, 2000);
    }

    // Cleanup on unmount
    return () => {
      if (timeoutRef.current) clearTimeout(timeoutRef.current);
    };
  }, [color]);

  return (
    <div
      className="color-display"
      style={{
        backgroundColor: displayColor,
        opacity: opacity,
        transition: "opacity 1s ease", // Smooth fade-out transition
      }}
    >
      {/* Optional: Add content or leave empty */}
    </div>
  );
}
