import React, { useState, useEffect } from "react";

export default function TimerDisplay({ initialTime = 10, onTimerEnd }) {
  const [timer, setTimer] = useState(initialTime); // Initialize the timer with the provided initial time

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1); // Decrement timer by 1
      }, 1000);

      return () => clearInterval(interval); // Cleanup interval on unmount or when timer changes
    } else if (onTimerEnd) {
      onTimerEnd(); // Notify the parent when the timer reaches zero
    }
  }, [timer, onTimerEnd]);

  return (
    <div className="timer-display">
      <p style={{ fontSize: "2em", fontWeight: "bold" }}>
        {timer > 0 ? `Timer: ${timer}s` : "Time's up!"}
      </p>
    </div>
  );
}
