import React, { useEffect, useRef } from "react";

const ColorPicker = ({ onColorChange }) => {
  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");

    const drawGradient = () => {
      ctx.clearRect(0, 0, canvas.width, canvas.height);
      const gradient = ctx.createLinearGradient(0, 0, canvas.width, 0);

      // Add color stops for all specified colors
      gradient.addColorStop(0.00, '#FF0000');    // Red
      gradient.addColorStop(0.10, '#FFFF00');    // Yellow
      gradient.addColorStop(0.20, '#00FF00');    // Green
      gradient.addColorStop(0.30, '#0000FF');    // Blue
      gradient.addColorStop(0.40, '#4B0082');    // Indigo
      gradient.addColorStop(0.50, '#EE82EE');    // Violet
      gradient.addColorStop(0.60, '#FFFFFF');    // White
      gradient.addColorStop(0.70, '#000000');    // Black
      gradient.addColorStop(0.80, '#A52A2A');    // Brown
      gradient.addColorStop(0.90, '#FFC0CB');    // Pink

      ctx.fillStyle = gradient;
      ctx.fillRect(0, 0, canvas.width, canvas.height);
    };

    const resizeCanvas = () => {
      const parent = canvas.parentElement;
      canvas.width = parent.offsetWidth;
      canvas.height = parent.offsetHeight;
      drawGradient();
    };

    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);
    return () => window.removeEventListener("resize", resizeCanvas);
  }, []);

  const handleCanvasClick = (event) => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    const rect = canvas.getBoundingClientRect();
    const x = event.clientX - rect.left;
    const y = event.clientY - rect.top;
    const pixel = ctx.getImageData(x, y, 1, 1).data;
    const [r, g, b, a] = pixel;

    if (a === 0) return; // Ignore transparent clicks

    const hexColor = `#${((1 << 24) + (r << 16) + (g << 8) + b)
      .toString(16)
      .slice(1)
      .padStart(6, '0')}`;

    onColorChange(hexColor);
  };

  return (
    <canvas
      ref={canvasRef}
      className="color-picker"
      onClick={handleCanvasClick}
    />
  );
};

export default ColorPicker;
