import React, { useEffect, useState, useRef } from "react";

export default function ColorDisplay({ color }) {
  const [displayColor, setDisplayColor] = useState("#FFFFFF");
  const [opacity, setOpacity] = useState(1);
  const fadeTimeoutRef = useRef(null);
  const colorTimeoutRef = useRef(null);

  useEffect(() => {
    // Clear previous timers
    if (fadeTimeoutRef.current) clearTimeout(fadeTimeoutRef.current);
    if (colorTimeoutRef.current) clearTimeout(colorTimeoutRef.current);

    // Reset to white and full opacity
    setDisplayColor("#FFFFFF");
    setOpacity(1);

    if (color && color.toUpperCase() !== "#FFFFFF") {
      // Set the new color after a short delay to ensure reset
      colorTimeoutRef.current = setTimeout(() => {
        setDisplayColor(color);

        // Start fade-out timer after color is set
        fadeTimeoutRef.current = setTimeout(() => {
          setOpacity(0);
        }, 2000);
      }, 0);
    }

    // Cleanup timers on unmount or color change
    return () => {
      if (fadeTimeoutRef.current) clearTimeout(fadeTimeoutRef.current);
      if (colorTimeoutRef.current) clearTimeout(colorTimeoutRef.current);
    };
  }, [color]);

  return (
    <div
      className="color-display"
      style={{
        backgroundColor: displayColor,
        opacity: opacity,
        transition: "opacity 1s ease",
        width: "100px",    // Example size
        height: "100px",   // Example size
      }}
    />
  );
}
