import React, { useState, useEffect } from "react";
import "./TimerDisplay.css";

export default function TimerDisplay({ initialTime = 10, onTimerEnd }) {
  const [timer, setTimer] = useState(initialTime);
  const [isEnding, setIsEnding] = useState(false);

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer - 1);

        // Add warning effect when timer is low
        if (timer <= 3 && !isEnding) {
          setIsEnding(true);
        }
      }, 1000);

      return () => clearInterval(interval);
    } else if (onTimerEnd) {
      onTimerEnd();
    }
  }, [timer, onTimerEnd, isEnding]);

  // Calculate progress width (100% at start, 0% at end)
  const progressWidth = (timer / initialTime) * 100;

  return (
    <div className="timer-container">
      {/* Animated progress bar */}
      <div 
        className="timer-progress" 
        style={{ 
          transform: `scaleX(${progressWidth / 100})`,
          opacity: timer > 3 ? 0.3 : 0.5 // Increase opacity when time is running out
        }}
      />

      <div className="timer-content">
        {timer > 0 ? (
          <>
            <div className="timer-seconds" style={{ 
              color: timer <= 3 ? '#ff3333' : '#333' 
            }}>
              {timer}
            </div>
            <div className="timer-label">
              seconds remaining
            </div>
          </>
        ) : (
          <div className="times-up">Time's up!</div>
        )}
      </div>
    </div>
  );
}
