import React, { useEffect, useRef, useState } from "react";
import "./TimerDisplay.css";

export default function TimerDisplay({ initialTime = 10, onTimerEnd }) {
  const [timer, setTimer] = useState(initialTime);
  const canvasRef = useRef(null);

  useEffect(() => {
    if (timer > 0) {
      const interval = setInterval(() => setTimer((t) => t - 1), 1000);
      return () => clearInterval(interval);
    } else if (onTimerEnd) {
      onTimerEnd();
    }
  }, [timer, onTimerEnd]);

  // Draw the circular countdown ring
  useEffect(() => {
    const canvas = canvasRef.current;
    if (!canvas) return;
    const ctx = canvas.getContext("2d");

    // Set up high-DPI scaling for the canvas
    const scale = window.devicePixelRatio || 1;
    const width = canvas.width / scale;
    const height = canvas.height / scale;
    canvas.width = width * scale;
    canvas.height = height * scale;
    ctx.scale(scale, scale);

    const centerX = width / 2;
    const centerY = height / 2;
    const radius = Math.min(centerX, centerY) * 0.85;
    const lineWidth = radius * 0.18;
    const timeRatio = timer / initialTime;

    ctx.clearRect(0, 0, width, height);

    // Draw background ring (light gray)
    ctx.beginPath();
    ctx.arc(centerX, centerY, radius, 0, 2 * Math.PI);
    ctx.lineWidth = lineWidth;
    ctx.strokeStyle = "#e0e0e0";
    ctx.stroke();

    // Color logic: green → yellow → red
    let color;
    if (timeRatio > 0.6) color = "#00CC00";
    else if (timeRatio > 0.3) color = "#FFCC00";
    else color = "#FF3333";

    // Draw the countdown arc (clockwise)
    if (timeRatio > 0) {
      const startAngle = -Math.PI / 2;
      const endAngle = startAngle + 2 * Math.PI * timeRatio;
      ctx.beginPath();
      ctx.arc(centerX, centerY, radius, startAngle, endAngle, false);
      ctx.lineWidth = lineWidth;
      ctx.strokeStyle = color;
      ctx.lineCap = "round";
      ctx.stroke();
    }

    // Draw timer text in the center
    ctx.fillStyle = timer <= 3 ? "#ff3333" : "#333333";
    ctx.font = `bold ${Math.floor(radius * 0.5)}px Arial`; // Dynamically scale font size
    ctx.textAlign = "center";
    ctx.textBaseline = "middle";
    ctx.fillText(timer > 0 ? timer : "0", centerX, centerY);
  }, [timer, initialTime]);

  return (
    <div className="timer-container">
      <canvas
        ref={canvasRef}
        width={200}
        height={200}
        style={{
          position: "relative",
          zIndex: 2,
          width: "80%",
          height: "80%",
          background: "none",
        }}
      />
      {/* Timer text label below the canvas */}
      <div className="timer-content">
        <div className="timer-label">
          {timer > 0 ? "seconds remaining" : "Time's up!"}
        </div>
      </div>
    </div>
  );
}
