import React, { useState, useEffect } from "react";
import "./assets/styles/main.css";
import { colorDistance } from "./utils/colorUtils";
import SenderScreen from "./screens/SenderScreen";
import ReceiverScreen from "./screens/ReceiverScreen";
import TimerDisplay from "./components/TimerDisplay/TimerDisplay";

// Animal images
import dog from "./assets/images/dog.jpg";
import butterfly from "./assets/images/butterfly.jpg";
import giraffe from "./assets/images/giraffe.png";
import deer from "./assets/images/deer.png";
import elephant from "./assets/images/elephant.jpg";

// Animal data
const ANIMALS = [
  { name: "Dog", image: dog, color: "#deb887" },
  { name: "Giraffe", image: giraffe, color: "#f4e285" },
  { name: "Butterfly", image: butterfly, color: "#ff69b4" },
  { name: "Deer", image: deer, color: "#a0522d" },
  { name: "Elephant", image: elephant, color: "#b0b0b0" },
];

const COLOR_TOLERANCE = 60;

export default function App() {
  const [role, setRole] = useState("sender"); // Current role: "sender" or "receiver"
  const [senderColor, setSenderColor] = useState("#FFFFFF"); // Color selected by the sender
  const [score, setScore] = useState(0); // Current score
  const [timer, setTimer] = useState(10); // Countdown timer
  const [refreshPositions, setRefreshPositions] = useState(0); // Trigger for refreshing animal positions

  // Reset the color display to default after 2 seconds when the role changes
  useEffect(() => {
    const timeout = setTimeout(() => {
      setSenderColor("#FFFFFF"); // Reset to default color after 2 seconds
    }, 2000);

    return () => clearTimeout(timeout); // Cleanup timeout on role change or unmount
  }, [role]);

  // Handle timer end (when timer reaches zero)
  const handleTimerEnd = () => {
    // Switch roles when the timer ends
    setRole((prevRole) => (prevRole === "sender" ? "receiver" : "sender"));
    setTimer(10); // Reset timer to 10 seconds
    setRefreshPositions((prev) => prev + 1); // Trigger animal position refresh
  };

  // Handle color change by the sender
  const handleColorChange = (newColor) => {
    setSenderColor(newColor); // Update the sender's selected color
    setRole("receiver"); // Switch to receiver role
    setTimer(10); // Reset the timer
    setRefreshPositions((prev) => prev + 1); // Randomize animal positions
  };

  // Handle color matching and scoring by the receiver
  const handleColorMatch = (animalColor) => {
    const distance = colorDistance(senderColor, animalColor);
    if (distance <= COLOR_TOLERANCE) {
      setScore((prevScore) => prevScore + 1); // Increment score if the color matches
    }
    setRole("sender"); // Switch to sender role
    setTimer(10); // Reset the timer
    setRefreshPositions((prev) => prev + 1); // Randomize animal positions
  };

  return (
    <div className="app-root">
      {role === "sender" ? (
        <SenderScreen
          animals={ANIMALS}
          refreshPositions={refreshPositions}
          timer={timer}
          senderColor={senderColor}
          score={score}
          onColorChange={handleColorChange}
          timerDisplay={
            <TimerDisplay
              timer={timer}
              setTimer={setTimer}
              onTimerEnd={handleTimerEnd}
            />
          }
        />
      ) : (
        <ReceiverScreen
          animals={ANIMALS}
          refreshPositions={refreshPositions}
          timer={timer}
          senderColor={senderColor}
          score={score}
          onAnimalSelect={(animal) => handleColorMatch(animal.color)}
          timerDisplay={
            <TimerDisplay
              timer={timer}
              setTimer={setTimer}
              onTimerEnd={handleTimerEnd}
            />
          }
        />
      )}
    </div>
  );
}
